    private void btnBuscarIngredienteSedeDeseosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscarIngredienteSedeDeseosActionPerformed

        String cad = (this.txtBuscarIngredienteSedeDeseos.getText());
        int pos = Test.ai.buscarIngredienteXCodigo(cad);
        if (pos == -1) {
            Utilitaria.imprimirEnVentanaInformativa("Debe digitar un cÃ³digo valido", "AtenciÃ³n");

        } else {

            Utilitaria.imprimirEnVentanaInformativa(" Un (1) ingrediente encontrado", "AtenciÃ³n");

            txtAreaIngredientesSedePrincipal.append("CÃDIGO: \t\t" + Test.ai.arr_i[ pos].codigo + "\nNOMBRE: \t\t" + Test.ai.arr_i[ pos].getNombre()
                    + "\nDESCRIPCIÃN: \t" + Test.ai.arr_i[ pos].descripcion + " \nUNIDAD DE MEDIDA: \t" + Test.ai.arr_i[ pos].unidadMedida
                    + "\nCOSTO: \t\t" + Test.ai.arr_i[ pos].getCosto() + " pesos" + "\nSTOCK: \t\t" + Test.ai.arr_i[ pos].stock
                    + " en existencia" + " \nGRUPO ALIMENTO: \t" + Test.ai.arr_i[ pos].getGrupoAlimento());

        }

        this.txtBuscarIngredienteSedeDeseos.setText("");
    }//GEN-LAST:event_btnBuscarIngredienteSedeDeseosActionPerformed

